SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION san_cons_sinal_vital_tri ( ie_sinal_vital_p text, qt_quantidade_p bigint ) RETURNS varchar AS $body$
DECLARE


qt_minima_w	double precision;
qt_maxima_w	double precision;
ds_mensagem_w	varchar(255);

/*
PS - PA Sistólica
PD - PA Diastólica
P - Pulso
T - Temperatura
V - Volume
*/
BEGIN

if (ie_sinal_vital_p IS NOT NULL AND ie_sinal_vital_p::text <> '') then

	select	coalesce(max(qt_minimo),0),
			coalesce(max(qt_maximo),9999)
	into STRICT 	qt_minima_w,
			qt_maxima_w
	from   	san_regra_sinal_vital
	where 	ie_sinal_vital	= ie_sinal_vital_p
	and		ie_situacao 	= 'A';

	if ((qt_quantidade_p < qt_minima_w) or (qt_quantidade_p > qt_maxima_w)) then
		if (ie_sinal_vital_p = 'PS') then
			ds_mensagem_w := wheb_mensagem_pck.get_Texto(285000);
		elsif (ie_sinal_vital_p = 'PD')then
			ds_mensagem_w := wheb_mensagem_pck.get_Texto(285001);
		elsif (ie_sinal_vital_p = 'P') then
			ds_mensagem_w := wheb_mensagem_pck.get_Texto(285002);
		elsif (ie_sinal_vital_p = 'T') then
			ds_mensagem_w := wheb_mensagem_pck.get_Texto(285003);
		elsif (ie_sinal_vital_p = 'V') then
			ds_mensagem_w := wheb_mensagem_pck.get_Texto(285004);
		elsif (ie_sinal_vital_p = 'F') then
			ds_mensagem_w := wheb_mensagem_pck.get_Texto(285005);
		end if;
	end if;
end if;

commit;

return ds_mensagem_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
