CREATE OR REPLACE FUNCTION san_doacao_inutil_valida ( nr_seq_doacao_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(1);

BEGIN

ds_retorno_w := 'S';

if (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') then

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ds_retorno_w
from	san_producao a
where	a.nr_seq_doacao = nr_seq_doacao_p
and	coalesce(a.nr_seq_inutil::text, '') = ''
and	coalesce(a.dt_conferencia::text, '') = '';

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
