SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION san_exame_pai_positivo (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
cd_pessoa_fisica_w	varchar(10);
ie_existe_exame_w	varchar(1);


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	select	max(cd_pessoa_fisica)
	into STRICT	cd_pessoa_fisica_w
	from	san_reserva
	where	nr_sequencia = nr_sequencia_p;

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_existe_exame_w
	FROM	san_exame_lote a,
		san_exame_realizado b,
		san_exame c,
		san_reserva d
	WHERE	a.nr_seq_reserva	= d.nr_sequencia
	AND	b.nr_seq_exame_lote 	= a.nr_sequencia
	AND	b.nr_seq_exame  	= c.nr_sequencia
	AND	c.ie_tipo_exame 	= '2'
	and	d.cd_pessoa_fisica	= cd_pessoa_fisica_w
	and	UPPER(b.ds_resultado) in ('POSITIVO','NEGATIVO');

	if (ie_existe_exame_w = 'S') then
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ds_retorno_w
		FROM	san_exame_lote a,
			san_exame_realizado b,
			san_exame c,
			san_reserva d
		WHERE	a.nr_seq_reserva	= d.nr_sequencia
		AND	b.nr_seq_exame_lote 	= a.nr_sequencia
		AND	b.nr_seq_exame  	= c.nr_sequencia
		AND	c.ie_tipo_exame 	= '2'
		AND	UPPER(b.ds_resultado) 	= 'POSITIVO'
		and	d.cd_pessoa_fisica	= cd_pessoa_fisica_w;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
