CREATE OR REPLACE FUNCTION san_existe_nr_sangue_atx ( nr_seq_doacao_p bigint, nr_sangue_p text) RETURNS bigint AS $body$
DECLARE
nr_seq_doacao_w		san_doacao.nr_sequencia%type;

BEGIN

nr_seq_doacao_w := nr_seq_doacao_p;

if (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') and (nr_sangue_p IS NOT NULL AND nr_sangue_p::text <> '') then

	select	CASE WHEN coalesce(max(nr_sequencia)::text, '') = '' THEN  nr_seq_doacao_p  ELSE max(nr_sequencia) END
	into STRICT	nr_seq_doacao_w
	from	san_doacao
	where	nr_sangue = nr_sangue_p
	and	nr_sequencia <> nr_seq_doacao_p;
end if;

return	nr_seq_doacao_w;

end;
