SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION san_inapto_def_temp ( nr_seq_doacao_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(2);

BEGIN

if (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') then

		select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'D' END
		into STRICT	ie_retorno_w
		from 	san_impedimento c,
			san_doacao_impedimento b,
			san_doacao a
		where 	a.nr_sequencia 		= b.nr_seq_doacao
		and 	b.nr_seq_impedimento 	= c.nr_sequencia
		and 	a.nr_sequencia  	= nr_seq_doacao_p
		and 	a.ie_avaliacao_final 	<> 'A'
		and	c.ie_definitivo = 'S';


	if (ie_retorno_w = 'N') then

		select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'D' END
		into STRICT	ie_retorno_w
		from 	san_impedimento c,
			san_questionario b,
			san_doacao a
		where 	a.nr_sequencia 		= b.nr_seq_doacao
		and 	b.nr_seq_impedimento 	= c.nr_sequencia
		and 	a.nr_sequencia  	= nr_seq_doacao_p
		and 	a.ie_avaliacao_final 	<> 'A'
		and	c.ie_definitivo = 'S'
		and     coalesce(b.ie_impede_doacao,'N') = 'S';

	end if;

	if (ie_retorno_w = 'N') then

		select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'D' END
		into STRICT	ie_retorno_w
		from 	san_impedimento_sorologia b,
			san_doacao a
		where 	a.nr_sequencia 		= b.nr_seq_doacao
		and 	a.nr_sequencia  	= nr_seq_doacao_p
		and 	a.ie_avaliacao_final 	<> 'A'
		and	coalesce(b.ie_inapto_definitivo,'N') = 'S';

	end if;

	if (ie_retorno_w = 'N') then

		select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'T' END
		into STRICT	ie_retorno_w
		from 	san_impedimento c,
			san_doacao_impedimento b,
			san_doacao a
		where 	a.nr_sequencia 		= b.nr_seq_doacao
		and 	b.nr_seq_impedimento 	= c.nr_sequencia
		and 	a.nr_sequencia  	= nr_seq_doacao_p
		and 	a.ie_avaliacao_final 	<> 'A'
		and	c.ie_definitivo = 'N';

	end if;

	if (ie_retorno_w = 'N') then

		select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'T' END
		into STRICT	ie_retorno_w
		from 	san_impedimento c,
			san_questionario b,
			san_doacao a
		where 	a.nr_sequencia 		= b.nr_seq_doacao
		and 	b.nr_seq_impedimento 	= c.nr_sequencia
		and 	a.nr_sequencia  	= nr_seq_doacao_p
		and 	a.ie_avaliacao_final 	<> 'A'
		and	c.ie_definitivo = 'N'
		and (b.dt_ocorrencia + c.nr_dias_inaptidao) > clock_timestamp()
		and     coalesce(b.ie_impede_doacao,'N') = 'S';

	end if;

	if (ie_retorno_w = 'N') then

		select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'T' END
		into STRICT	ie_retorno_w
		from	san_impedimento_sorologia
		where	nr_seq_doacao = nr_seq_doacao_p
		and	ie_nova_amostra = 'N'
		and	dt_liberacao > clock_timestamp();

	end if;

	if (ie_retorno_w = 'N') then

		select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'DS' END  -- Por motivo de desistÃªncia
		into STRICT	ie_retorno_w
		from 	san_doacao a
		where 	a.nr_sequencia  	= nr_seq_doacao_p
		and	(a.nr_motivo_desistencia IS NOT NULL AND a.nr_motivo_desistencia::text <> '');

	end if;


end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION san_inapto_def_temp ( nr_seq_doacao_p text) FROM PUBLIC;

