CREATE OR REPLACE FUNCTION san_marca_qt_validade ( nr_marca_p bigint ) RETURNS bigint AS $body$
DECLARE


retorno_w			bigint;


BEGIN

	if (nr_marca_p IS NOT NULL AND nr_marca_p::text <> '') then
		begin
			select	max(qt_validade_bolsa)
			into STRICT	retorno_w
			from	SAN_MARCA_BOLSA
			where	ie_situacao = 'A'
			and 	nr_sequencia = nr_marca_p;
		end;
	end if;

return	retorno_w;

end;
$body$
LANGUAGE PLPGSQL
