CREATE OR REPLACE FUNCTION san_obter_agrupamento_ok ( nr_seq_producao_p bigint, nr_seq_agrupameto_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_retorno_w	varchar(1);
					

BEGIN 
 
if (nr_seq_producao_p IS NOT NULL AND nr_seq_producao_p::text <> '') and (nr_seq_agrupameto_p IS NOT NULL AND nr_seq_agrupameto_p::text <> '') then 
	begin 
	select	'S' 
	into STRICT	ie_retorno_w 
	from	san_producao_v a, 
		san_agrupamento b 
	where	a.nr_seq_derivado 	= b.nr_seq_derivado 
	and	a.ie_tipo_sangue	= b.ie_tipo_sangue 
	and	a.ie_fator_rh		= b.ie_fator_rh 
	and	a.nr_sequencia 		= nr_seq_producao_p 
	and	b.nr_sequencia 		= nr_seq_agrupameto_p 
	and not	exists (	SELECT	1 
			from	san_agrupamento_derivado x 
			where	x.nr_seq_producao = a.nr_sequencia)  LIMIT 1;
	exception 
	when others then 
		ie_retorno_w :=	'N';
	end;
end if;
 
return	ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
