CREATE OR REPLACE FUNCTION san_obter_agrup_exame ( nr_seq_tranfusao_p bigint, nr_seq_reserva_p bigint, nr_seq_producao_p bigint) RETURNS varchar AS $body$
DECLARE


nr_agrupamento_w	bigint;
nr_seq_reserva_w	bigint;

BEGIN

select 	max(nr_seq_reserva)
into STRICT	nr_seq_reserva_w
from	san_transfusao
where	nr_sequencia	=	nr_seq_tranfusao_p;

select 	max(nr_agrupamento)
into STRICT	nr_agrupamento_w
from 	san_reserva_prod
where	nr_seq_reserva = coalesce(nr_seq_reserva_p,nr_seq_reserva_w)
and	nr_seq_producao	= nr_seq_producao_p;

return nr_agrupamento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
