CREATE OR REPLACE FUNCTION san_obter_bolsas_transf (nr_seq_transfusao_p bigint) RETURNS varchar AS $body$
DECLARE


nr_sangue_w	varchar(255);			
ds_retorno_W	varchar(2000);

C01 CURSOR FOR
	SELECT	nr_sangue || ', '
	from	san_producao
	where	nr_seq_transfusao = nr_seq_transfusao_p;


BEGIN

ds_retorno_w := '';

open C01;
loop
fetch C01 into	
	nr_sangue_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	
	ds_retorno_w := ds_retorno_w || nr_sangue_w;
	
	end;
end loop;
close C01;

return	substr(ds_retorno_w,1,length(ds_retorno_w)-2);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
