CREATE OR REPLACE FUNCTION san_obter_classif_exame (nr_seq_exame_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(80);


BEGIN

/* D - Descrição  / C - Cor */

if (ie_opcao_p = 'D') then

	select	substr(max(a.ds_classificacao_exame),1,80)
	into STRICT	ds_retorno_w
	from	san_classificacao_exame a,
		san_exame b
	where	b.nr_seq_classif_exame = a.nr_sequencia
	and	b.nr_sequencia = nr_seq_exame_p;

elsif (ie_opcao_p = 'C') then

	select	substr(max(a.ds_cor),1,80)
	into STRICT	ds_retorno_w
	from	san_classificacao_exame a,
		san_exame b
	where	b.nr_seq_classif_exame = a.nr_sequencia
	and	b.nr_sequencia = nr_seq_exame_p;

end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
