CREATE OR REPLACE FUNCTION san_obter_cq_tempo_armazen (nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


qt_armazenamento_qual_w	integer;
			

BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	select	CASE WHEN trunc(coalesce(max(a.dt_saida_armazenamento),clock_timestamp())-max(a.dt_realizacao))=0 THEN null END  qt
		into STRICT	qt_armazenamento_qual_w
	from	san_controle_qualidade a
	where	a.nr_sequencia = nr_sequencia_p;

end if;

return	qt_armazenamento_qual_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
