SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION san_obter_dados_conferencia (nr_seq_doacao_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


nm_usuario_conferencia_w	varchar(15);
nm_usuario_conferencia_seg_w	varchar(15);
dt_conferencia_w	timestamp;
dt_conferencia_seg_w	timestamp;
ds_retorno_w	varchar(30);


BEGIN
ds_retorno_w := '';

if (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') then

	select	nm_usuario_conferencia,
		nm_usuario_conferencia_seg,
		dt_conferencia,
		dt_conferencia_seg
	into STRICT	nm_usuario_conferencia_w,
		nm_usuario_conferencia_seg_w,
		dt_conferencia_w,
		dt_conferencia_seg_w
	from	san_doacao
	where	nr_sequencia = nr_seq_doacao_p;

	if (ie_opcao_p = 'UC') then
		ds_retorno_w := nm_usuario_conferencia_w;
	elsif (ie_opcao_p = 'UCS') then
		ds_retorno_w := nm_usuario_conferencia_seg_w;
	elsif (ie_opcao_p = 'DC') then
		ds_retorno_w := to_char(dt_conferencia_w,'dd/mm/yyyy');
	elsif (ie_opcao_p = 'DCS') then
		ds_retorno_w := to_char(dt_conferencia_seg_w,'dd/mm/yyyy');
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
