CREATE OR REPLACE FUNCTION san_obter_dados_derivado ( nr_seq_derivado_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
ie_reproducao_auto_w	varchar(1);


BEGIN
if (nr_seq_derivado_p IS NOT NULL AND nr_seq_derivado_p::text <> '') then
	select	ie_reproducao_auto
	into STRICT	ie_reproducao_auto_w
	from	san_derivado
	where	nr_sequencia = nr_seq_derivado_p;

	if (ie_opcao_p = 'R') then
		ds_retorno_w := ie_reproducao_auto_w;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
