CREATE OR REPLACE FUNCTION san_obter_dados_estoque (nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

nr_seq_armazenamento_w	bigint;
qt_dose_estoque_w	bigint;
ds_local_W	varchar(255);
nm_usuario_ent_estoque_w	varchar(15);
dt_entrada_estoque_w	timestamp;
ds_retorno_w	varchar(255);


BEGIN
/* 'A' armazenamento
     'D' dose
     'DT'  data entrada estoque
     'NM' usu√°rio estoque     */
ds_retorno_w := '';
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') then
	select	qt_dose_estoque,
		nr_seq_armazenamento,
		dt_entrada_estoque,
		nm_usuario_ent_estoque
	into STRICT	qt_dose_estoque_w,
		nr_seq_armazenamento_w,
		dt_entrada_estoque_w,
		nm_usuario_ent_estoque_w
	from	san_producao
	where	nr_sequencia = nr_sequencia_p;

	if (ie_opcao_p = 'A') and (nr_seq_armazenamento_w IS NOT NULL AND nr_seq_armazenamento_w::text <> '') then
		begin
		select	ds_local
		into STRICT	ds_local_w
		from	san_armazenamento_bolsa
		where	nr_sequencia = nr_seq_armazenamento_w;

		ds_retorno_w := ds_local_w;
		end;
	elsif (ie_opcao_p = 'D') then
		ds_retorno_w := qt_dose_estoque_w;
	elsif (ie_opcao_p = 'DT') then
		ds_retorno_w := to_char(dt_entrada_estoque_w,'dd/mm/yyyy');
	elsif (ie_opcao_p = 'NM') then
		ds_retorno_w := nm_usuario_ent_estoque_w;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
