CREATE OR REPLACE FUNCTION san_obter_data_entrada_estoque ( nr_seq_producao_p bigint, nr_seq_emp_ent_p bigint) RETURNS timestamp AS $body$
DECLARE


nr_seq_emp_ent_w	bigint;
dt_entrada_w		timestamp := clock_timestamp() + interval '9999 days';


BEGIN

if (coalesce(nr_seq_emp_ent_p::text, '') = '') then
	select	nr_seq_emp_ent,
		dt_producao
	into STRICT	nr_seq_emp_ent_w,
		dt_entrada_w
	from san_producao
	where nr_sequencia = nr_seq_producao_p;
else
	nr_seq_emp_ent_w	:= nr_seq_emp_ent_p;
end if;

if (nr_seq_emp_ent_w IS NOT NULL AND nr_seq_emp_ent_w::text <> '') then
	select	dt_emprestimo
	into STRICT	dt_entrada_w
	from	san_emprestimo
	where	nr_sequencia = nr_seq_emp_ent_w
	  and	ie_entrada_saida = 'E';
end if;

return dt_entrada_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
