CREATE OR REPLACE FUNCTION san_obter_data_saida_estoque ( nr_seq_producao_p bigint, nr_seq_transfusao_p bigint, nr_seq_emp_saida_p bigint, nr_seq_inutil_p bigint) RETURNS timestamp AS $body$
DECLARE


nr_seq_transfusao_w	bigint;
nr_seq_emp_saida_w	bigint;
nr_seq_inutil_w	bigint;

dt_saida_w		timestamp := clock_timestamp() + interval '9999 days';


BEGIN

if (coalesce(nr_seq_transfusao_p::text, '') = '') and (coalesce(nr_seq_emp_saida_p::text, '') = '') and (coalesce(nr_seq_inutil_p::text, '') = '') then
	select	nr_seq_transfusao,
		nr_seq_emp_saida,
		nr_seq_inutil
	into STRICT	nr_seq_transfusao_w,
		nr_seq_emp_saida_w,
		nr_seq_inutil_w
	from san_producao
	where nr_sequencia = nr_seq_producao_p;
else
	nr_seq_transfusao_w	:= nr_seq_transfusao_p;
	nr_seq_emp_saida_w	:= nr_seq_emp_saida_p;
	nr_seq_inutil_w		:= nr_seq_inutil_p;
end if;

if (nr_seq_transfusao_w IS NOT NULL AND nr_seq_transfusao_w::text <> '') then
	select	dt_transfusao
	into STRICT	dt_saida_w
	from	san_transfusao
	where	nr_sequencia = nr_seq_transfusao_w;
elsif (nr_seq_emp_saida_w IS NOT NULL AND nr_seq_emp_saida_w::text <> '') then
	select	dt_emprestimo
	into STRICT	dt_saida_w
	from	san_emprestimo
	where	nr_sequencia = nr_seq_emp_saida_w
	  and	ie_entrada_saida = 'S';
elsif (nr_seq_inutil_w IS NOT NULL AND nr_seq_inutil_w::text <> '') then
	select	dt_inutilizacao
	into STRICT	dt_saida_w
	from	san_inutilizacao
	where	nr_sequencia = nr_seq_inutil_w;
end if;

return dt_saida_w;

end;
$body$
