CREATE OR REPLACE FUNCTION san_obter_doacao_sorologia (cd_pessoa_fisica_p bigint, nr_seq_doacao_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_doacao_w		bigint;
ie_retorno_w		varchar(1);
nr_seq_doacao_amostra_w	bigint;


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	select 	max(nr_sequencia)
	into STRICT	nr_seq_doacao_w
	from   	san_doacao
	where  	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	nr_sequencia 	<> nr_seq_doacao_p;

	select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END a
	into STRICT	ie_retorno_w
	from   	san_exame_lote a,
		san_exame_realizado b
	where  	a.nr_sequencia 	   = b.nr_seq_exame_lote
	and	a.nr_seq_doacao    = nr_seq_doacao_w
	and	consiste_doacao_exame(b.nr_seq_exame, b.ds_resultado) = 'S';
end if;

if (ie_retorno_w = 'N') then
	nr_seq_doacao_w := 0;
else
	select 	max(nr_seq_doacao_amostra)
	into STRICT	nr_seq_doacao_amostra_w
	from   	san_doacao
	where  	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	nr_sequencia = nr_seq_doacao_w;

	if (nr_seq_doacao_amostra_w IS NOT NULL AND nr_seq_doacao_amostra_w::text <> '') then
		nr_seq_doacao_w := nr_seq_doacao_amostra_w;
	end if;
end if;

return	nr_seq_doacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
