CREATE OR REPLACE FUNCTION san_obter_doador_cad_dia (cd_pessoa_fisica_p text, nr_seq_doacao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_cadastrado_w	varchar(2) := '';
qtd_w		bigint;


BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	select	count(*)
	into STRICT	qtd_w
	from	san_doacao a
	where	a.dt_doacao between trunc(clock_timestamp())+1/86399 and trunc(clock_timestamp())+86399/86400
	and	a.cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	a.nr_sequencia		<> nr_seq_doacao_p;

	if (qtd_w > 0) then

		select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'LN' END
		into STRICT	ie_cadastrado_w
		from	san_doacao x
		where	x.cd_pessoa_fisica 	= cd_pessoa_fisica_p
		and	x.nr_sequencia		<> nr_seq_doacao_p
		and	coalesce(x.dt_fim_coleta::text, '') = ''
		and	x.dt_doacao between trunc(clock_timestamp())+1/86399 and trunc(clock_timestamp())+86399/86400;

		if (ie_cadastrado_w = 'N') then

			select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
			into STRICT	ie_cadastrado_w
			from	san_doacao x
			where	x.cd_pessoa_fisica 	= cd_pessoa_fisica_p
			and	x.nr_sequencia		<> nr_seq_doacao_p
			and	(x.dt_fim_coleta IS NOT NULL AND x.dt_fim_coleta::text <> '')
			and	x.dt_doacao between trunc(clock_timestamp())+1/86399 and trunc(clock_timestamp())+86399/86400;
		end if;

	end if;

end if;

return	ie_cadastrado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
