CREATE OR REPLACE FUNCTION san_obter_doador ( nr_Seq_Doacao_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
/* 
Opcao 
C - CÃ³digo 
N - Nome 
*/
				 
				 
cd_pessoa_Fisica_w	varchar(10);				
ds_retorno_w		varchar(255);
				

BEGIN 
 
select	cd_pessoa_fisica 
into STRICT	cd_pessoa_Fisica_w 
from	san_doacao a 
where	a.nr_Sequencia = nr_seq_doacao_p;
 
if (ie_opcao_p = 'C') then 
	ds_Retorno_w := cd_pessoa_Fisica_w;
elsif (ie_opcao_p = 'N') then 
	ds_Retorno_w := obter_nome_pf(cd_pessoa_Fisica_w);
end if;
 
return ds_retorno_w;
 
end;
