CREATE OR REPLACE FUNCTION san_obter_entidade_pf ( cd_pessoa_fisica_p text) RETURNS bigint AS $body$
DECLARE


nr_seq_entidade_w	bigint;


BEGIN

	if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

		select	max(nr_seq_entidade)
		into STRICT	nr_seq_entidade_w
		from	san_reserva
		where	cd_pessoa_fisica	=	cd_pessoa_fisica_p;

		if (coalesce(nr_seq_entidade_w::text, '') = '') then

			select	max(nr_seq_entidade)
			into STRICT	nr_seq_entidade_w
			from	san_transfusao
			where	cd_pessoa_fisica	=	cd_pessoa_fisica_p;
		end if;

	end if;

return	nr_seq_entidade_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
