SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION san_obter_estagio_reserva ( nr_seq_reserva_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w			varchar(1);
ie_status_w			varchar(1);
ie_possui_hemocomp_w		varchar(1);
dt_coleta_receptor_w		timestamp;
ie_transfusao_finalizada_w	varchar(1);


BEGIN

if (nr_seq_reserva_p IS NOT NULL AND nr_seq_reserva_p::text <> '') then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_possui_hemocomp_w
	from    san_reserva_prod
	where	nr_seq_reserva = nr_seq_reserva_p;

	select	ie_status,
		dt_coleta_receptor
	into STRICT	ie_status_w,
		dt_coleta_receptor_w
	from	san_reserva
	where	nr_sequencia = nr_seq_reserva_p;

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_transfusao_finalizada_w
	from	san_transfusao
	where	nr_seq_reserva = nr_seq_reserva_p
	and	ie_status = 'F';

	if (ie_possui_hemocomp_w = 'N') then
		ie_retorno_w := 'G';
	end if;

	if (ie_possui_hemocomp_w = 'S') and (coalesce(dt_coleta_receptor_w::text, '') = '') then
		ie_retorno_w := 'C';
	end if;

	if (ie_possui_hemocomp_w = 'S') and (dt_coleta_receptor_w IS NOT NULL AND dt_coleta_receptor_w::text <> '') then
		ie_retorno_w := 'T';
	end if;

	if (ie_status_w = 'T') and (ie_transfusao_finalizada_w = 'N') then
		ie_retorno_w := 'I';
	end if;

	if (ie_status_w = 'T') and (ie_transfusao_finalizada_w = 'S') then
		ie_retorno_w := 'F';
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
