SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION san_obter_exames_anteriores ( nr_seq_doacao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(4000);
cd_pessoa_fisica_w	varchar(10);
ds_exame_anterior_w	varchar(400);

C01 CURSOR FOR
	SELECT	h.ds_exame||' - '||substr(san_obter_result_exame(a.nr_seq_exame_lote, a.nr_seq_exame),1,254)
	from	san_exame_realizado a,
		san_exame_lote b,
		san_doacao c,
		san_exame h
	where	a.nr_seq_exame_lote = b.nr_sequencia
	and	b.nr_seq_doacao = c.nr_sequencia
	and	a.nr_seq_exame = h.nr_sequencia
	and	c.nr_sequencia = (	SELECT	max(d.nr_sequencia)
					from	san_exame_realizado e,
						san_exame_lote f,
						san_doacao d,
						san_exame g
					where	e.nr_seq_exame_lote = f.nr_sequencia
					and	f.nr_seq_doacao = d.nr_sequencia
					and	e.nr_seq_exame = g.nr_sequencia
					and	d.cd_pessoa_fisica = cd_pessoa_fisica_w
					and	d.nr_sequencia <> nr_seq_doacao_p
					and	a.nr_seq_exame = e.nr_seq_exame
					and	coalesce(g.ie_mostra_iniciar_triagem,'S') = 'S'
					and	(e.dt_liberacao IS NOT NULL AND e.dt_liberacao::text <> '')
					and	coalesce(CASE WHEN g.ie_resultado='N' THEN  to_char(e.vl_resultado)  ELSE e.ds_resultado END , e.ie_resultado) is not null);


BEGIN

if (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') then

	select	max(cd_pessoa_fisica)
	into STRICT	cd_pessoa_fisica_w
	from	san_doacao
	where	nr_sequencia = nr_seq_doacao_p;

	open C01;
	loop
	fetch C01 into
		ds_exame_anterior_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		ds_retorno_w := ds_retorno_w || ds_exame_anterior_w || chr(10);

		end;
	end loop;
	close C01;

end if;

if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
	ds_retorno_w := wheb_mensagem_pck.get_texto(309690) || ':' || chr(10) || chr(10) || ds_retorno_w; -- Exames anteriores
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION san_obter_exames_anteriores ( nr_seq_doacao_p bigint) FROM PUBLIC;

