CREATE OR REPLACE FUNCTION san_obter_fenotipagem_padrao ( nr_seq_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


retorno_w		varchar(255);
ds_fenotipo_aw_w	varchar(255);
result_fenotipagem_w	varchar(255);

c01 CURSOR FOR
	SELECT	ds_fenotipo_aw
	from 	san_reg_padrao_fenotipagem
	where	ie_situacao = 'A'
	and 	ds_fenotipo_fr = result_fenotipagem_w;


BEGIN

IF (nr_seq_sequencia_p IS NOT NULL AND nr_seq_sequencia_p::text <> '') THEN

  select	replace(substr(san_obter_result_fenotipagem(nr_seq_sequencia_p, 1), 1, 255), ' ', '')
		into STRICT	result_fenotipagem_w
;

	if (result_fenotipagem_w IS NOT NULL AND result_fenotipagem_w::text <> '') then
		open c01;
		loop
		fetch c01 into
			ds_fenotipo_aw_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

			if ( coalesce(retorno_w::text, '') = '' ) then
				retorno_w := ds_fenotipo_aw_w;
			else
				retorno_w := substr(retorno_w || ', ' || ds_fenotipo_aw_w, 1, 255);
			end if;
		end;
		end loop;
		close c01;
	end if;

end if;

return retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
