CREATE OR REPLACE FUNCTION san_obter_fenotipagem_pf (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_fenotipagem_w		varchar(255) := null;

BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	select	max(san_obter_result_fenotipagem(c.nr_sequencia))
	into STRICT	ds_fenotipagem_w
	from	san_result_fenotipagem c,
		san_transfusao b,
		atendimento_paciente a
	where	1 = 1
	and	c.nr_seq_transfusao    = b.nr_sequencia
	and	b.nr_atendimento       = a.nr_atendimento
	and	a.cd_pessoa_fisica     = cd_pessoa_fisica_p
    and (c.dt_liberacao IS NOT NULL AND c.dt_liberacao::text <> '')
    and coalesce(c.dt_inativacao::text, '') = '';

	if (coalesce(ds_fenotipagem_w::text, '') = '') then
		select	max(san_obter_result_fenotipagem(b.nr_sequencia))
		into STRICT	ds_fenotipagem_w
		from	san_result_fenotipagem b,
			san_reserva a
		where	1 = 1
		and	b.nr_seq_reserva       = a.nr_sequencia
		and	a.cd_pessoa_fisica     = cd_pessoa_fisica_p
        and (b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '')
        and coalesce(b.dt_inativacao::text, '') = '';
	end if;

	if (coalesce(ds_fenotipagem_w::text, '') = '') then
		select	max(san_obter_result_fenotipagem(b.nr_sequencia))
		into STRICT	ds_fenotipagem_w
		from	san_result_fenotipagem b,
			san_doacao a
		where	1 = 1
		and	b.nr_seq_doacao	       = a.nr_sequencia
		and	a.cd_pessoa_fisica     = cd_pessoa_fisica_p
        and (b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '')
        and coalesce(b.dt_inativacao::text, '') = '';
	end if;

end if;

return	ds_fenotipagem_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
