CREATE OR REPLACE FUNCTION san_obter_inapto_concatenado (nr_seq_doacao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_motivo_inapto_w		varchar(90);
ds_motivo_inapto_ant_w		varchar(4000);
C01 CURSOR FOR
	SELECT distinct	ds_motivo_inaptidao
	from   san_questionario a,
	       san_motivo_inaptidao b
	where  a.nr_seq_motivo_inaptidao = b.nr_sequencia
	and    a.nr_seq_doacao = nr_seq_doacao_p
	order by 1;

BEGIN
if (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') then
	ds_motivo_inapto_ant_w := '';
	open C01;
	loop
	fetch C01 into
		ds_motivo_inapto_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		ds_motivo_inapto_ant_w := ds_motivo_inapto_ant_w||', '||ds_motivo_inapto_w;
		end;
	end loop;
	close C01;

	ds_motivo_inapto_ant_w := substr(ds_motivo_inapto_ant_w,2,4000);

end if;

return	ds_motivo_inapto_ant_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
