CREATE OR REPLACE FUNCTION san_obter_local_transf ( NR_SEQ_ATEND_PAC_UNID_P bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(100);


BEGIN
if (NR_SEQ_ATEND_PAC_UNID_P IS NOT NULL AND NR_SEQ_ATEND_PAC_UNID_P::text <> '') then
	begin

	select 	substr(obter_nome_setor(cd_setor_atendimento),1,25)  || ' - ' ||  to_char(dt_entrada_unidade,'dd/mm/yyyy hh24:mi:ss') || ' - ' || cd_unidade_basica || ' - '  || cd_unidade_compl
	into STRICT	ds_retorno_w
	from 	atend_paciente_unidade
	where 	nr_seq_interno = nr_seq_atend_pac_unid_p;
	end;
end if;

RETURN ds_retorno_w;
END;
$body$
