CREATE OR REPLACE FUNCTION san_obter_origem_reserva (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
nr_prescricao_w		bigint;
nr_seq_agenda_w		san_reserva.nr_seq_agenda%type;
				

BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	select	max(nr_prescricao),
		max(nr_seq_agenda)
	into STRICT	nr_prescricao_w,
		nr_seq_agenda_w
	from	san_reserva
	where	nr_sequencia = nr_sequencia_p;

	if (nr_seq_agenda_w IS NOT NULL AND nr_seq_agenda_w::text <> '') then
		ds_retorno_w := wheb_mensagem_pck.get_texto(309693); -- Agenda
	elsif (nr_prescricao_w IS NOT NULL AND nr_prescricao_w::text <> '') then
		ds_retorno_w := wheb_mensagem_pck.get_texto(309694); -- Prescricao
	else
		ds_retorno_w := wheb_mensagem_pck.get_texto(309697); -- Hemoterapia
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
