CREATE OR REPLACE FUNCTION san_obter_possui_exame_pai (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(1);
cd_pessoa_fisica_w	varchar(10);
nr_seq_exame_lote_w	bigint;

 
C01 CURSOR FOR 
	SELECT	b.nr_sequencia 
	from	san_reserva a, 
		san_exame_lote b 
	where	a.cd_pessoa_fisica = cd_pessoa_fisica_w 
	and	a.nr_sequencia = b.nr_seq_reserva 
	
union
 
	SELECT	b.nr_sequencia 
	from	san_transfusao a, 
		san_exame_lote b 
	where	cd_pessoa_fisica = cd_pessoa_fisica_w 
	and	a.nr_sequencia = b.nr_seq_transfusao;	
			 

BEGIN 
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
 
	select	max(cd_pessoa_fisica) 
	into STRICT	cd_pessoa_fisica_w 
	from	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_p;
 
	 
	open C01;
	loop 
	fetch C01 into	 
		nr_seq_exame_lote_w;
	EXIT WHEN NOT FOUND or ds_retorno_w = 'S';  /* apply on C01 */
		begin 
		 
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
		into STRICT	ds_retorno_w 
		from	san_exame_lote a, 
			san_exame_realizado b, 
			san_exame c 
		where	a.nr_sequencia = nr_seq_exame_lote_w 
		and	b.nr_seq_exame_lote = a.nr_sequencia 
		and	b.nr_seq_exame = c.nr_sequencia 
		and	c.ie_tipo_exame = 2 
		and	upper(b.ds_resultado) = 'POSITIVO';			
		 
		end;
	end loop;
	close C01;
	 
else 
	ds_retorno_w := 'N';
end if;	
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION san_obter_possui_exame_pai (nr_atendimento_p bigint) FROM PUBLIC;

