CREATE OR REPLACE FUNCTION san_obter_prod_doacao_emp_ent (nr_seq_producao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_reg_doacao_w		varchar(1);

BEGIN
if (nr_seq_producao_p IS NOT NULL AND nr_seq_producao_p::text <> '') then
	select	CASE WHEN ie=1 THEN 'S' WHEN ie=2 THEN 'N' END
	into STRICT	ie_reg_doacao_w
	from (SELECT	1 ie
		from	san_producao b,
			san_doacao a
		where	b.nr_seq_doacao	= a.nr_sequencia
		and	b.nr_sequencia	= nr_seq_producao_p
		
union

		SELECT	2 ie
		from	san_producao b,
			san_emprestimo a
		where	b.nr_seq_emp_ent	= a.nr_sequencia
		and	b.nr_sequencia		= nr_seq_producao_p) alias1
	where	(ie IS NOT NULL AND ie::text <> '');
end if;

return ie_reg_doacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
