CREATE OR REPLACE FUNCTION san_obter_qtd_regra_solic ( nr_seq_derivado_p bigint, ie_tipo_sangue_p text, ie_fator_rh_p text, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


qt_estoque_min_w	integer;
qt_estoque_max_w	integer;
qt_retorno_w		integer;

BEGIN

select	max(qt_estoque_min),
	max(qt_estoque_max)
into STRICT	qt_estoque_min_w,
	qt_estoque_max_w
from	san_gerar_solic_hemoc
where	nr_seq_derivado = nr_seq_derivado_p
and	ie_tipo_sangue  = ie_tipo_sangue_p
and	ie_fator_rh	= ie_fator_rh_p;

if (ie_opcao_p = 'MI') then
	qt_retorno_w := qt_estoque_min_w;
elsif (ie_opcao_p = 'MA') then
	qt_retorno_w := qt_estoque_max_w;
end if;

return	qt_retorno_w;

end;
$body$
