CREATE OR REPLACE FUNCTION san_obter_receptor ( nr_seq_transfusao_p bigint, nr_seq_reserva_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w	varchar(255);			
			 

BEGIN 
if (nr_seq_transfusao_p IS NOT NULL AND nr_seq_transfusao_p::text <> '') and (nr_seq_transfusao_p <> 0)	then 
	 
	if (ie_opcao_p = 'C') then 
		select	max(cd_pessoa_fisica) 
		into STRICT	ds_retorno_w 
		from	san_transfusao 
		where	nr_sequencia = nr_seq_transfusao_p;
	elsif (ie_opcao_p = 'D') then 
		select	substr(obter_nome_pf(max(cd_pessoa_fisica)),1,255) 
		into STRICT	ds_retorno_w 
		from	san_transfusao 
		where	nr_sequencia = nr_seq_transfusao_p;	
	end if;
end if;	
if (nr_seq_reserva_p IS NOT NULL AND nr_seq_reserva_p::text <> '') and (nr_seq_reserva_p <> 0 ) then 
	if (ie_opcao_p = 'C') then 
		select	max(cd_pessoa_fisica) 
		into STRICT	ds_retorno_w 
		from	san_reserva 
		where	nr_sequencia = nr_seq_reserva_p;
	elsif (ie_opcao_p = 'D') then 
		select	substr(obter_nome_pf(max(cd_pessoa_fisica)),1,255) 
		into STRICT	ds_retorno_w 
		from	san_reserva 
		where	nr_sequencia = nr_seq_reserva_p;
		 
	end if;
end if;	
 
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
