CREATE OR REPLACE FUNCTION san_obter_resultado_procergs ( nr_seq_doacao_p bigint, ie_tipo_exame_p bigint) RETURNS varchar AS $body$
DECLARE

-- ie_tipo_exame_p = valor do dom√≠nio: SAN - Tipo de exame
ds_retorno_w		varchar(3);
nr_seq_exame_lote_w	san_exame_lote.nr_sequencia%type;

BEGIN

if (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') and (ie_tipo_exame_p IS NOT NULL AND ie_tipo_exame_p::text <> '') then

	select	max(nr_sequencia)
	into STRICT	nr_seq_exame_lote_w
	from	san_exame_lote
	where	nr_seq_doacao = nr_seq_doacao_p;

	if (nr_seq_exame_lote_w IS NOT NULL AND nr_seq_exame_lote_w::text <> '') then
		select	CASE WHEN max(upper(a.ds_resultado))='POSITIVO' THEN  'POS' WHEN max(upper(a.ds_resultado))='NEGATIVO' THEN  'NEG' WHEN max(upper(a.ds_resultado)) IS NULL THEN  'NAR'  ELSE ' ' END
		into STRICT	ds_retorno_w
		from	san_exame_realizado a,
			san_exame b
		where	a.nr_seq_exame_lote = nr_seq_exame_lote_w
		and	a.nr_seq_exame = b.nr_sequencia
		and	b.ie_tipo_exame = ie_tipo_exame_p  LIMIT 1;
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
