SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION san_obter_result_leucometria ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


vl_retorno_w		varchar(4000);
ie_tipo_coleta_w	smallint;
qt_coletada_w		smallint;
qt_leucometria_c1_w	bigint;
qt_v2_w			bigint;



BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	select	max(a.ie_tipo_coleta),
		max(a.qt_coletada)
	into STRICT 	ie_tipo_coleta_w,
		qt_coletada_w
	from	san_doacao a,
		san_leucometria b
	where	a.nr_sequencia = b.nr_seq_doacao
	and	b.nr_sequencia = nr_sequencia_p;

	select 	max(a.qt_leucometria_c1)
	into STRICT	qt_leucometria_c1_w
	from	san_leucometria a
	where	a.nr_sequencia	= nr_sequencia_p;

	qt_v2_w	:= dividir((qt_coletada_w*qt_leucometria_c1_w),450);

	if (ie_tipo_coleta_w IS NOT NULL AND ie_tipo_coleta_w::text <> '') then
		if ( ie_tipo_coleta_w = '4') then
			SELECT	max(wheb_mensagem_pck.get_texto(803074, 'QT_COLETADA='||round((qt_coletada_w/6)::numeric,2)))
			into STRICT	vl_retorno_w
			;
		else
			if (qt_v2_w < qt_coletada_w) then

				select	wheb_mensagem_pck.get_texto(803075, 'QT_COLETADA='||round((qt_coletada_w - qt_v2_w)::numeric,2))
				into STRICT	vl_retorno_w
				;
			else
				select 	wheb_mensagem_pck.get_texto(803076,
								'QT_COLETADA_1='||round((qt_v2_w - qt_coletada_w)::numeric,2)||
								';QT_COLETADA_2='||round(((5*(qt_v2_w - qt_coletada_w))/20),2)||
								';QT_COLETADA_3='||round(((qt_v2_w - qt_coletada_w)-((5*(qt_v2_w - qt_coletada_w))/20)),2))
				into STRICT	vl_retorno_w
				;
			end if;
		end if;
	end if;
end if;
return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION san_obter_result_leucometria ( nr_sequencia_p bigint) FROM PUBLIC;

