DECLARE


qtd_doacao_w		varchar(10);

BEGIN

select 	count(*)
into STRICT	qtd_doacao_w
from 	san_doacao
where 	nr_sequencia <= nr_seq_doacao_p
and	(dt_coleta IS NOT NULL AND dt_coleta::text <> '')
