CREATE OR REPLACE FUNCTION san_obter_se_amostra_conta (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

dt_validade_w	timestamp;
ie_retorno_w	varchar(1);

C01 CURSOR FOR
	SELECT  dt_validade
	from    san_amostra_receptor
	where   cd_pessoa_fisica = cd_pessoa_fisica_p
	and	(dt_validade IS NOT NULL AND dt_validade::text <> '')
	and	ie_gerar_conta = 'S'
	and	coalesce(ie_amostra_conta,'N') = 'N';

BEGIN
ie_retorno_w 	:= 'N';

select  CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_retorno_w
from    san_amostra_receptor
where   cd_pessoa_fisica = cd_pessoa_fisica_p
and	coalesce(dt_validade::text, '') = ''
and	ie_gerar_conta = 'S'
and	coalesce(ie_amostra_conta,'N') = 'N';

open C01;
loop
fetch C01 into
	dt_validade_w;
EXIT WHEN NOT FOUND or ie_retorno_w = 'S';  /* apply on C01 */
	begin
	if (clock_timestamp() <= dt_validade_w) then
		ie_retorno_w := 'S';
	end if;
	end;
end loop;
close C01;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
