CREATE OR REPLACE FUNCTION san_obter_se_amostra_reagente ( ie_resultado_p text, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


ds_retorno_w			smallint;

/*
ie_opcao_p
  'POS' - Positivos (Reagentes)
  'NEG' - Negativos
  'TPN' - Todos os realizados (Pos + Neg)
*/
BEGIN

if (ie_opcao_p = 'POS') then
	if (ie_resultado_p = ie_opcao_p) then
		ds_retorno_w := 1;
	else
		ds_retorno_w := 0;
	end if;
elsif (ie_opcao_p = 'NEG') then
	if (ie_resultado_p = ie_opcao_p) then
		ds_retorno_w := 1;
	else
		ds_retorno_w := 0;
	end if;
elsif (ie_opcao_p = 'TPN') then
	if (ie_resultado_p = 'NEG') then
		ds_retorno_w := 1;
	elsif (ie_resultado_p = 'POS') then
		ds_retorno_w := 1;
	else
		ds_retorno_w := 0;
	end if;
end if;

return ds_retorno_w;

END;
