CREATE OR REPLACE FUNCTION san_obter_se_amostra_recebida ( cd_pessoa_fisica_p text, dt_parametro_p timestamp default null) RETURNS varchar AS $body$
DECLARE


dt_validade_w				timestamp;
dt_recebimento_w			timestamp;
ie_retorno_w				varchar(1) := 'N';
ie_registrar_coleta_receptor_w		varchar(1);
nr_sequencia_w				bigint;


BEGIN

ie_registrar_coleta_receptor_w := obter_param_usuario(450, 75, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_registrar_coleta_receptor_w);

if (ie_registrar_coleta_receptor_w = 'P') AND (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') THEN

	SELECT  MAX(nr_sequencia)
	INTO STRICT	nr_sequencia_w
	FROM    san_amostra_receptor
	WHERE   cd_pessoa_fisica = cd_pessoa_fisica_p;

	select  max(dt_recebimento)
	into STRICT	dt_recebimento_w
	from    san_amostra_receptor
	where   nr_sequencia = nr_sequencia_w;

end if;

if (dt_recebimento_w IS NOT NULL AND dt_recebimento_w::text <> '') then

	ie_retorno_w := 'S';

end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
