CREATE OR REPLACE FUNCTION san_obter_se_amostra_valida ( cd_pessoa_fisica_p text, dt_parametro_p timestamp default null) RETURNS varchar AS $body$
DECLARE


dt_validade_w				timestamp;
ie_retorno_w				varchar(1) := 'N';
ie_registrar_coleta_receptor_w		varchar(1);

BEGIN

ie_registrar_coleta_receptor_w := obter_param_usuario(450, 75, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_registrar_coleta_receptor_w);

if (ie_registrar_coleta_receptor_w = 'P') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	select  max(dt_validade)
	into STRICT	dt_validade_w
	from    san_amostra_receptor
	where   cd_pessoa_fisica = cd_pessoa_fisica_p
	and	(dt_validade IS NOT NULL AND dt_validade::text <> '');

elsif (ie_registrar_coleta_receptor_w <> 'P') and (dt_parametro_p IS NOT NULL AND dt_parametro_p::text <> '') then

	dt_validade_w := san_obter_validade_amostra(dt_parametro_p);

end if;

if (dt_validade_w IS NOT NULL AND dt_validade_w::text <> '') and (dt_validade_w >= clock_timestamp()) then

	ie_retorno_w := 'S';

end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
