CREATE OR REPLACE FUNCTION san_obter_se_doador_impedido ( cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1);

BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_retorno_w
	from 	san_impedimento c,
		san_doacao_impedimento b,
		san_doacao a
	where 	a.nr_sequencia 		= b.nr_seq_doacao
	and 	b.nr_seq_impedimento 	= c.nr_sequencia
	and 	a.cd_pessoa_fisica 	= cd_pessoa_fisica_p
	and 	a.ie_avaliacao_final 	<> 'A'
	and	c.ie_definitivo = 'S';


	if (ie_retorno_w = 'N') then

		select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_retorno_w
		from 	san_impedimento c,
			san_questionario b,
			san_doacao a
		where 	a.nr_sequencia 		= b.nr_seq_doacao
		and 	b.nr_seq_impedimento 	= c.nr_sequencia
		and 	a.cd_pessoa_fisica 	= cd_pessoa_fisica_p
		and 	a.ie_avaliacao_final 	<> 'A'
		and	c.ie_definitivo = 'S'
		and     coalesce(b.ie_impede_doacao,'N') = 'S';

	end if;

	if (ie_retorno_w = 'N') then

		select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_retorno_w
		from 	san_impedimento_sorologia b,
			san_doacao a
		where 	a.nr_sequencia 		= b.nr_seq_doacao
		and 	a.cd_pessoa_fisica 	= cd_pessoa_fisica_p
		and 	a.ie_avaliacao_final 	<> 'A'
		and	coalesce(b.ie_inapto_definitivo,'N') = 'S';

	end if;

end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
