SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION san_obter_se_doador_trali ( cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1);


BEGIN

ie_retorno_w := 'N';

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	begin
		SELECT 'S'
		into STRICT	ie_retorno_w
		FROM	san_producao a,
			san_trans_reacao b,
			san_tipo_reacao c,
			san_doacao	d
		WHERE  	a.nr_sequencia     	= b.nr_seq_producao
		AND	b.nr_seq_reacao	  	= c.nr_sequencia
		AND	d.nr_sequencia		= a.nr_seq_doacao
		AND	coalesce(c.ie_trali,'N') 	= 'S'
		and	d.cd_pessoa_fisica	= cd_pessoa_fisica_p;
	exception
		when others then
			ie_retorno_w := 'N';
	end;
end if;


return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
