CREATE OR REPLACE FUNCTION san_obter_se_hemo_bloqueado (nr_seq_producao_p bigint) RETURNS varchar AS $body$
DECLARE

qt_valida_w	bigint;
ie_bloqueio_w	varchar(1);

BEGIN
ie_bloqueio_w := 'N';
if (nr_seq_producao_p IS NOT NULL AND nr_seq_producao_p::text <> '') then
	select	count(*)
	into STRICT	qt_valida_w
	from	san_producao
	where	nr_sequencia = nr_seq_producao_p
	and	(dt_bloqueio IS NOT NULL AND dt_bloqueio::text <> '');

	if (qt_valida_w > 0) then
		ie_bloqueio_w := 'S';
	end if;
end if;

return	ie_bloqueio_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
