CREATE OR REPLACE FUNCTION san_obter_se_hem_transf ( nr_seq_producao_p bigint) RETURNS varchar AS $body$
DECLARE

ie_possui_transfusao_w varchar(1);


BEGIN
	if (nr_seq_producao_p IS NOT NULL AND nr_seq_producao_p::text <> '') then
		select  CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_possui_transfusao_w
		from	san_producao
		where 	nr_sequencia = nr_seq_producao_p
		and	(nr_seq_transfusao IS NOT NULL AND nr_seq_transfusao::text <> '');

		end if;

return ie_possui_transfusao_w;

END;
$body$
LANGUAGE PLPGSQL
