CREATE OR REPLACE FUNCTION san_obter_se_permite_susp_adep (nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_resultado_w		varchar(1)	:= 'S';


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then

	select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
	into STRICT	ds_resultado_w
	from	prescr_proc_hor a
	where	a.nr_prescricao = nr_prescricao_p
	and	(a.dt_fim_horario IS NOT NULL AND a.dt_fim_horario::text <> '');

end if;

return	ds_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
