CREATE OR REPLACE FUNCTION san_obter_se_pode_produzir ( nr_seq_doacao_p bigint, nr_seq_hemocomponente_p bigint) RETURNS varchar AS $body$
DECLARE


ie_tipo_bolsa_doacao_w		san_doacao.ie_tipo_bolsa%type;
nr_seq_derivado_doacao_w	san_doacao.nr_seq_derivado%type;
ds_retorno_w			varchar(1) := 'N';

BEGIN

if (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') then

	select	max(ie_tipo_bolsa),
		max(nr_seq_derivado_padrao)
	into STRICT	ie_tipo_bolsa_doacao_w,
		nr_seq_derivado_doacao_w
	from	san_doacao
	where	nr_sequencia = nr_seq_doacao_p;

	if (nr_seq_derivado_doacao_w IS NOT NULL AND nr_seq_derivado_doacao_w::text <> '') then

		select 	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
		into STRICT	ds_retorno_w
		from	san_derivado_reproducao
		where	nr_seq_derivado = nr_seq_derivado_doacao_w
		and	ie_situacao = 'A';

		if (ds_retorno_w = 'N') then

			select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
			into STRICT	ds_retorno_w
			from	san_derivado_reproducao
			where	nr_seq_derivado = nr_seq_derivado_doacao_w
			and (coalesce(ie_tipo_bolsa, 0) = coalesce(ie_tipo_bolsa_doacao_w, 0)
				or coalesce(ie_tipo_bolsa::text, '') = '')
			and	nr_seq_derivado_rep = nr_seq_hemocomponente_p
			and	ie_situacao = 'A';

		end if;

	end if;

end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
