CREATE OR REPLACE FUNCTION san_obter_se_possui_ocorrencia (nr_atendimento_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(1);
cd_pessoa_fisica_w	varchar(10);


BEGIN 
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
 
	select	max(cd_pessoa_fisica) 
	into STRICT	cd_pessoa_fisica_w 
	from	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_p;
 
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
	into STRICT	ds_retorno_w 
	from	san_ocorrencia_doador a 
	where (exists (SELECT	1 
			from  	san_doacao x  
			where 	x.nr_sequencia = a.nr_seq_doacao  
			and	x.cd_pessoa_fisica = cd_pessoa_fisica_w) or  
		exists (select 	1 
			from  	san_reserva z  
			where 	z.nr_sequencia = a.nr_seq_reserva   
			and	z.cd_pessoa_fisica = cd_pessoa_fisica_w) or   		   
		exists (select 1  
			from	san_transfusao y,  
				atendimento_paciente p  
			where 	p.nr_atendimento 	= y.nr_atendimento  
			and  y.nr_sequencia  = a.nr_seq_transfusao  
			and	 p.cd_pessoa_fisica = cd_pessoa_fisica_w));
else 
	ds_retorno_w := 'N';
end if;	
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
