CREATE OR REPLACE FUNCTION san_obter_se_reacao (nr_seq_producao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1)	:= 'N';


BEGIN

if (nr_seq_producao_p IS NOT NULL AND nr_seq_producao_p::text <> '') then
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	san_trans_reacao a,
		san_tipo_reacao b
	where	a.nr_seq_reacao = b.nr_sequencia
	and	coalesce(b.ie_considera_reacao,'S') = 'S'
	and	a.nr_seq_producao = nr_seq_producao_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
