CREATE OR REPLACE FUNCTION san_obter_se_reserva_parcial (nr_seq_reserva_p bigint) RETURNS varchar AS $body$
DECLARE


qt_solicitada_w		san_reserva_item.qt_solicitada%type;
qt_hemocomponte_w	bigint;
ds_retorno_w		varchar(1);


BEGIN

select 	sum(QT_SOLICITADA)
into STRICT	qt_solicitada_w
from	SAN_RESERVA_ITEM
where	nr_seq_reserva = nr_seq_reserva_p;

select 	count(*)
into STRICT	qt_hemocomponte_w
from	SAN_RESERVA_prod
where	nr_seq_reserva = nr_seq_reserva_p;

ds_retorno_w	:= 'N';

if (qt_hemocomponte_w < qt_solicitada_w) then
	ds_retorno_w	:= 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
