CREATE OR REPLACE FUNCTION san_obter_se_tipo_impedimento ( nr_seq_doacao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
ie_definitivo_w		varchar(1);
ie_temporario_w		varchar(1);


BEGIN
if (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') then
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'D' END
	into STRICT	ie_definitivo_w
	from	san_impedimento b,
		san_questionario c
	where	coalesce(c.ie_impede_doacao,'N')	= 'S'
	and	c.nr_seq_impedimento		= b.nr_sequencia
	and	c.nr_seq_doacao			= nr_seq_doacao_p
	and	b.ie_definitivo			= 'S';

	ds_retorno_w	:= ie_definitivo_w;

	if (ie_definitivo_w =  'N') then
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'T' END
		into STRICT	ie_temporario_w
		from	san_impedimento b,
			san_questionario c
		where	coalesce(c.ie_impede_doacao,'N')	= 'S'
		and	c.nr_seq_impedimento		= b.nr_sequencia
		and	c.nr_seq_doacao			= nr_seq_doacao_p
		and	b.ie_definitivo			= 'N';

		ds_retorno_w	:= ie_temporario_w;
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
