SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION san_obter_se_trali_multigesta ( nr_seq_derivado_p bigint, nr_seq_doacao_p bigint) RETURNS varchar AS $body$
DECLARE



ie_opcao_w		varchar(1);
ie_lavado_w		varchar(1);
ie_industrializado_w	varchar(1);
ie_tipo_w		varchar(1);
cd_pessoa_fisica_w	varchar(10);
ds_texto_w		varchar(80);
ds_retorno_w		varchar(255);


BEGIN

if (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') then

	Select 	max(b.ie_opcao),
		max(b.ie_lavado),
		max(b.ie_industrializado),
		max(b.ds_texto)
	into STRICT	ie_opcao_w,
		ie_lavado_w,
		ie_industrializado_w,
		ds_texto_w
	from	san_regra_trali b
	where 	b.nr_seq_derivado 	= nr_seq_derivado_p;

	select 	max(cd_pessoa_fisica)
	into STRICT	cd_pessoa_fisica_w
	from	san_doacao
	where	nr_sequencia = nr_seq_doacao_p;

	if (ie_opcao_w = 'M') then
		ie_tipo_w:= obter_se_doadora_multigesta(nr_seq_doacao_p);
	elsif (ie_opcao_w = 'T') then
		ie_tipo_w := san_obter_se_doador_trali(cd_pessoa_fisica_w);
	else
		ie_tipo_w:= obter_se_doadora_multigesta(nr_seq_doacao_p);
		if (ie_tipo_w <> 'S') then
			ie_tipo_w := san_obter_se_doador_trali(cd_pessoa_fisica_w);
		end if;
	end if;


	if (ie_tipo_w = 'S') then -- Então se for doação Trali ou Multigesta
		ds_retorno_w := ds_texto_w;
	end if;
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
