SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION san_obter_se_volume_unid (nr_seq_doacao_p bigint) RETURNS bigint AS $body$
DECLARE


qt_volume_real_w	smallint;
nr_hemecomponente_w	bigint;


BEGIN
if (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') then
	select	x.qt_volume_real
	into STRICT	qt_volume_real_w
	from	san_doacao x
	where	1 = 1
	and	x.nr_sequencia	= nr_seq_doacao_p
	and	exists (SELECT	1
			from	san_producao a
			where	1 = 1
			and	a.nr_seq_derivado in (15,16)
			and	a.nr_seq_doacao	= x.nr_sequencia
			);

	if (qt_volume_real_w >= 400) then
		nr_hemecomponente_w := 16;
	elsif (qt_volume_real_w < 400) then
		nr_hemecomponente_w := 15;
	end if;

end if;

return	nr_hemecomponente_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
